mapper.update:
update banner
set status=#{status}
where id = #{id}

mapper.select:
select id,title,img_path imgPath,status,put_date putDate,description
from banner
where id = #{id}

select id,title,img_path imgPath,status,put_date putDate,description
from banner
order by id desc limit ${(page-1)*rows},${rows}
